import React, { useState } from 'react'
import useScreen from '../hook/useScreen'
import ButtonAdmin from './Button'
import { InputAdmin } from './Input'

function UsernameUI({username, handleFinish}) {
    const [user, setUser] = useState(username)
    const large = useScreen()
    return(
            <div className={` w-full flex flex-col gap-2 ${large&& 'pr-3'} ${!large && 'm'}`}>
                <p>Votre nom d'utilisateur</p>
                <form  style={{width:large?'49.4vh':'36.6vh'}} className={`${large ? 'w-full':'w-80'} flex  flex-col gap-2`} onSubmit={(e)=>{e.preventDefault(); handleFinish(user)}}>
                    <InputAdmin handleChange={(e)=>setUser(e.target.value)} type={'text'} value={user} placeholder={"Votre nom d'utilisateur"}/>
                    <div className=' flex justify-center'><ButtonAdmin validation={user.length >0} value={'Terminer'}/></div>
                </form>
        </div>
    )
}

export default UsernameUI
