import React, { useState } from 'react'
import { fetchLink } from '../Ttools'
import FramePage from '../ComponentsAdmin/FramePage'
import Navbar from '../ComponentsReno/Navbar'


function Login() {
  const [user, setUser] = useState('')
  const [password, setPassword] = useState('')
  const handleSubmit = async(e) =>{
    e.preventDefault()
    const formData = new FormData()
    formData.append('usernameOrEmail', user)
    formData.append('password', password)
    try{
      await fetch(fetchLink('login'), {body:formData})
      .then((value) =>value.json())
      .then(value =>{
        localStorage.setItem('token', value.token)
      })
    }
    catch(error){
      console.error(error.response.data)
    }

  }
  return (
        <>
          <Navbar right={false}/>
          <div className=' w-full h-full'>
          <div className='mb-1'>
                <p className=' text-center text-[23px]'> Bienvenue dans RenoluxAdmin </p>
                <p className=' my-3 text-center text-[18px]'>Creer votre compte pour continuer</p>
          </div>
          <div className=' flex justify-center w-full'>
          <form className=' flex flex-col gap-2 mb-2 w-1/4 justify-center' onSubmit={e =>handleSubmit(e)}>
              <input value={user} onChange={e =>setUser(e.target.value)} type='text' className=' p-2 rounded-md  focus:border-blue-600  outline-none border border-black' placeholder="Nom d'utilisateur ou email"/>
              <input value={password} onChange={e =>setPassword(e.target.value)} type='password' className=' p-2  rounded-md focus:border-blue-600  outline-none border border-black' placeholder='Mot de passe' />
              <div className=' flex justify-center'><button type='submit' className=' bg-blue-600 text-white p-2 rounded-lg w-1/4'>Connexion</button></div>
          </form>
          </div>
          <p>Mot de passe oublie? cliquez <span className=' text-blue-600 underline'>ici</span></p>
          <p className=' my-1'>Vous n'avez pas de compte? creer en un <span className=' text-blue-600 underline'>ici</span></p>
          </div>
        </>
  )
}

export default Login
