import { useEffect, useState } from "react";

export default function useScreen(){
    const [screen, setScreen] = useState({small:false, large:true})
    useEffect(() => {
        window.addEventListener('resize', ()=>{
            setScreen({small:!screen.small, large:!screen.large})
        })
    }, [screen])
    return screen
}
