import React, { useState } from 'react'
import Navbar from '../ComponentsReno/Navbar'
import Header from './Header'
import AttachFile from './AttachFile'
import HandleFile from './HandleFile'
import MediaContent from './MediaContent'

function Media() {
    const [active, setActive] = useState(0)
    const [file, setFile] = useState(undefined)
  return (
    <div>
        <Navbar/>
        <div className=' flex justify-center w-screen'>
          <Header active={active} handleActive={(val) => setActive(val)}/>
          <div className=' bg-black'><MediaContent active={active} /></div>
          {file && <HandleFile file={file} handleAnnuler={()=>setFile(undefined)}/>}
        </div>
    </div>
  )
}

export default Media
