import React, { useEffect, useState } from 'react'
import Navbar from '../ComponentsReno/Navbar';
import { fetchLink } from '../Ttools';
import { useNavigate } from 'react-router-dom';
import '../App.css'
import useScreen from '../hook/useScreen';
import Password from '../ComponentsAdmin/Password';
import SettingsIcon from '@mui/icons-material/Settings';


export const InputAdmin = ({handleChange, placeholder,value, type, autoComplete=''}) => {
    return(
        <input autoComplete={autoComplete}  onChange={(e) =>handleChange(e)} placeholder={placeholder} value={value} className=' w-full p-2 border border-black   rounded-md outlineInput' type={type}/>
    )
}

export const ButtonAdmin = ({value, validation}) =>{
    return(
        <button type='submit' disabled={!validation} className={` w-1/2 text-[18px]  p-2 cursor-pointer text-white font-semibold rounded-lg`} style={{backgroundColor:validation?'blue':'rgba(72, 149, 239, 1)'}}>{validation &&<SettingsIcon className=' text-white animate-spin'/>}{value}</button>
    )
}






const FormAccueil = ({emailValue, passord1Value, password2Value, email, password1, password2, handleVerification}) =>{
      const [visible, setVisible] = useState({p:true, v:true})
    const samePassword = password1 === password2
    const validPassword = password1.length > 5 && /\d/.test(password1) && /[A-Z]/.test(password1) && samePassword && validateEmail(email)

    const handleSubmit = (e) => {
        e.preventDefault()  
        handleVerification()
    }
    const large = useScreen()

    return(
        <>  
            <div className='mb-1 mt-9'>
