import React, { useState } from 'react'
import Navbar from '../ComponentsReno/Navbar';
import { fetchLink } from '../Ttools';
import { useNavigate } from 'react-router-dom';
import '../App.css'
import useScreen from '../hook/useScreen';
import Password from '../ComponentsAdmin/Password';


export const InputAdmin = ({handleChange, placeholder,value, type, autoComplete=''}) => {
    return(
        <input autoComplete={autoComplete}  onChange={(e) =>handleChange(e)} placeholder={placeholder} value={value} className=' w-full p-2 border border-black m-1 rounded-md outlineInput' type={type}/>
    )
}


export const validateEmail = (email) => {
    return String(email)
      .toLowerCase()
      .match(
        /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
      );
  };



export const ButtonAdmin = ({value, validation}) =>{
    return(
        <button type='submit' disabled={!validation} className={` w-1/2  p-2 cursor-pointer text-white font-semibold rounded-lg`} style={{backgroundColor:'rgba(72, 149, 239, 1)'}}>{value}</button>
    )
}



const Recommendation = ({message}) => <p style={{color:'rgba(80, 74, 74, 1)', fontSize:12}}>{message}</p>



const FormAccueil = ({emailValue, passord1Value, password2Value, email, password1, password2, handleVerification, handleGoogle}) =>{
    const [err, setErr] = useState({user:false, password1:false, password2:false})
    const samePassword = password1 === password2
    const validPassword = password1.length > 5 && /\d/.test(password1) && /[A-Z]/.test(password1) && samePassword && validateEmail(email)
    const handleSubmit = (e) => {
        e.preventDefault()  
        handleVerification()
    }
    const large = useScreen()
    return(
        <>  
