import React, { useEffect, useState } from 'react'
import { fetchLink, Timg, TypeWriterArr } from '../Ttools'

const Plans = () => {
    const [plans, setPlans] = useState([])
    useEffect(() => {
        fetch((fetchLink('medias/plans')), {
            method: 'GET',
        })
        .then((value)=> value.json())
        .then((value)=>{
            const plans = value
            const usefulPlans = plans.map(elt => fetchLink(`medias/plans/${elt}`))
            cons
            const items = usefulPlans.map((elt,indx) => <Timg key={indx} url={elt} className={' w-full h-full rounded-sm'} alt={`plan de placo n0${indx}`}/>)
            setPlans(items); 
            console.log(value); 
            })
        .catch((error) => console.log('An error occured!', error))
    }, [])

    if(plans.length <=0 ) return <div className=' flex justify-center items-center h-1/2'> <div className=' border-2 border-blue-500 w-10 h-10 animate-spin rounded-full border-b-white'></div></div>
    return(
        <div className=' border border-gray-200 p-2 rounded-sm flex flex-col gap-1 overflow-hidden scrollVertical'>
            <div className=' w-full h-full flex flex-col gap-1 scrollVertical '>
                {plans}
            </div>             
        </div>
    )

}

function HomeSectionRight(){
    return(
        <div className = 'col-span-4'>
            <div className='pt-4'>
                <span className=' text-transparent bg-clip-text bg-gradient-to-r from-blue-500 to-green-500 text-[55px]'>Nous sommes </span><br/>
                <span className=' bg-clip-text text-transparent bg-gradient-to-r from-blue-500 to-green-500 text-[55px]'>recommandés pour</span>
            </div>
            <div className=' flex justify-start pb-2'>
                <p className=' border border-black p-1 bg-gradient-to-r  from-blue-500 to bg-green-500'><TypeWriterArr arr={['Des solutions innovantes', 'Un service de qualité', 'le  respect des delais', 'Une approche orientée client.']} className={'text-[40px] font-bold text-white'} ms={100}/></p>
            </div>
            <Plans/>
        </div>
    )
}

function HomeSection() {
    // const large = useScreen()
    // if(large){
    //     return(
    //         <div>

    //         </div>
    //     )
    // }
  return (
    <div className='grid grid-cols-7'>
        <div className=' col-span-3 '> <img alt='' className='w-full h-full' src={fetchLink('medias/homepic.png')}/></div>
        <HomeSectionRight/>
    </div>
  )
}

export default HomeSection
