    .then((value) => {setCode2(value.data.code); console.log(`the code sent to email ${email} is ${value.data.code}`)})
    .catch(err => console.error(err))
    refCode.current.scrollIntoView({
        behavior: 'smooth',
        block: 'nearest',
        inline: 'center'
      });
}
function handleCode(code1){
    if(+code1 === code2){
      setValid(true)
    }
