import React, { useEffect, useState } from 'react'
import Navbar from '../ComponentsReno/Navbar';
import { fetchLink } from '../Ttools';
import { useNavigate } from 'react-router-dom';
import '../App.css'
import FormAccueil from './FormAccueil';
import VerificationCode from './VerificationCode';
import UsernameUI from './UsernameUI';
import RoundStep from './RoundStep';

function Register() {
    const [email, setEmail]= useState('')
    const [password1, setPassword1] = useState('')
    const [password2, setPassword2] = useState('')
    const [code, setCode] = useState(undefined)
    const [username, setUsername] = useState('')
    const [steps, setSteps] = useState({one:true, second:false})
    const [ui, setUI] = useState(0)
    const navigate = useNavigate()

    const checkUsername = () =>{
        return true 
    }
    const handleFinish = async(user) =>{
        const formData = new FormData()
        formData.append('username', user)
        formData.append('email', email)
        formData.append('password', password1)
        try{
           await fetch(fetchLink(''), {
                body:formData
            })
            .then(value =>value.json())
            .then(value => value)
            navigate('/admin')
        }
        catch(error){
            console.error(error.response.data)
        }

    }
    const handleNext = () =>{
        setTimeout(() => {
            setUI(ui + 1)
        }, 750);}

    useEffect(() => {
        fetch('http://localhost:3001/admin/connect',{credentials:'include'})
        .then(v => v.json())
        .then(value =>{
            setEmail(value.email)
            setUsername(value.given_name)
            setUI(3)
        } )
        .catch((err) => console.log(err.response.data))
        }
   ,[])
  return (
   <div>
    <div>
    <Navbar right = {false}/>
   {ui === 0 && <FormAccueil email= {email} password1 = {password1} password2 = {password2} emailValue = {value => setEmail(value)} passord1Value = {value => setPassword1(value)} password2Value={value =>setPassword2(value)} handleVerification={handleNext} />}
   {ui === 1 && <VerificationCode email={email} code={code} checkUsername={checkUsername} handleFunc={handleNext}/>}
   {ui === 2 && <UsernameUI username={/\w*/.exec(username)[0]} handleFinish={handleFinish}/>}

   <div>
{ui > 2 &&<div className=' mt-2 flex flex-row gap-2'>
                <RoundStep  step ={steps.one} value = {'1'}/>
                <RoundStep step ={steps.two} value = {'2'}/>
            </div>}
        {ui === 3 && <PasswordU handleVerification={()=>handleNext()} password1 = {password1} password2 = {password2} valuePassword1={value =>setPassword1(value)} valuePassword2={value =>setPassword2(value)} value={'Continuer'}/>}
        {ui === 4 && <UsernameUI username = {username} handleFinish = {handleFinish}/>}
   </div>
   </div>
   </div>
  )
}

export default Register
