import React, { useState } from 'react'
import { fetchLink, Rbutton, Timg } from '../Ttools'
import MenuIcon from '@mui/icons-material/Menu';
import useScreen from '../hook/useScreen';

const scroll = (id) => {
    const elt = document.getElementById(id)
    if(elt){
        elt.scrollIntoView({
            behavior:'smooth',
            block:'start'
        })
    }
}

export const  link = [{title:'Catalogue', link:'#catalogue'}, {title:'Obtenir un devis', link:'#devis'}, {title:'Contact & localisation', link:'#contact'}]

export function LeftNav(){
    return(
        <div className = 'flex flex-row items-center gap-2'>
            <Timg alt={'Logo Renolux'} url={fetchLink('medias/logo.png')} className={'w-7 h-7'}/>
            <p style={{color:'rgba(57, 55, 55, 1)'}} className=' font-semibold  text-lg'>Renolux Cameroun</p>
        </div>
    )
}

function RightNav(){
    const [sm, setSm] = useState(false)
    const large = useScreen()
    const links = link.map((elt, indx) =><a key={indx} href={elt.link} onClick={scroll(elt.link.slice(1))} style={{color:'rgba(57, 55, 55, 1)'}} className = 'text-sm no-underline'>{elt.title}</a>)
    if(large){
        return(<div className='flex flex-row gap-2 items-center'>{links}</div>)
    }
    return(
        <div>
            <button onClick={()=> setSm(!sm)} className=' p-2 border-x-gray-300'><MenuIcon className=' w-5 h-6'/></button>
            {sm && <div className=' absolute top-4 left-0 w-screen h-screen bg-white '>
                        <div className=' flex justify-center w-3/4 divide-y'>

                        </div>
                    </div>
            }
        </div>
    )
}

function Navbar() {
    const large = useScreen()

  return (
        <div className = 'flex justify-between px-2 py-3'>
            <LeftNav/>
            {large && <RightNav className={''}/>}
            {<div className = 'relative'>
                {sm && <NavSmallScreen handleClose = {() => setSm(!sm)}/>}  
            </div>}
        </div>
  )
}

export default Navbar
