import React, { useState } from 'react'
import useScreen from '../hook/useScreen'

function VerificationCode({email, code, handleFunc}) {
 const [num, setNum] = useState(1)
     const [one, setOne] = useState('')
     const [second, setSecond] = useState('')
     const [three, setThree] = useState('')
     const [four, setFour] = useState('')
     const [five, setFive] = useState('')
     const [six, setSix] = useState('')
     const [err, setErr] = useState(false)
    const large = useScreen()
    function handleKey(e){

    }
     const CaseS = ({indx, value, setValue }) => <div className={` p-1 border rounded-md ${indx === num || value ? 'border-blue-600':'border-black'}`}><input onKeyUp={(e) =>{if(!value && e.key === 'Backspace'){setNum(num - 1); console.log(num)}else{return;}}} type={large? 'text':'number'} value={value} autoFocus={indx === num} onChange={(e)=>setValue(e.target.value)} disabled={ indx !== num} className=' border-none outline-none text-[16px] w-10 h-10 p-4'/></div>

     const handleCode = (lastValue) =>{
         const codeA = String(one)+String(second)+String(three) + String(four) + String(five) + String(lastValue)
         if(code === codeA){
             handleFunc()
         }
         else{
             setOne(undefined); setSecond(undefined); setThree(undefined);setFour(undefined);setFive(undefined);setSix(undefined)
             setErr(true)
         }
     }
     return(
         <div className=' flex justify-center'>
            <div className={`${large && 'w-1/2'}`}>
                <p className=' mb-3 text-[21px] text-center'>Saississez le code de 06 chiffres envoye a l'addresse <span className=' font-bold'>{email}</span></p>
                <div className=' flex flex-row gap-2 justify-center'>
                <CaseS indx={1} value={one} setValue={(value) =>{setOne(value); setNum(num + 1)}}/>
                <CaseS indx={2} value={second} setValue={(value) =>{setSecond(value); setNum(num + 1)}}/>
                <CaseS indx={3} value={three} setValue={(value) =>{setThree(value); setNum(num + 1)}}/>
                <CaseS indx={4} value={four} setValue={(value) =>{setFour(value); setNum(num + 1)}}/>
                <CaseS indx={5} value={five} setValue={(value) =>{setFive(value); setNum(num + 1)}}/>
                <CaseS indx={6} value={six} setValue={(value) => handleCode(value)}/>
                </div>
                {err &&<p>Le code est  erron√©</p>}
                <p className=' mt-4 text-center'> vous n'avez pas recu de code? cliquez <span style={{color:'rgba(38, 103, 255, 1)'}}>ici</span></p>
            </div>
         </div>
     )
    }

export default VerificationCode
